mapper.select:
/** selectPetInfoCount **/

SELECT count(1) FROM pet_info
WHERE  1=1


AND pet_size = #{petSize}



AND pet_nm like '%' #{searchString} '%'

/** selectPetInfoList **/ SELECT pet_srl , pet_nm , (SELECT file_sys_nm FROM com_file_data WHERE file_tp = 'petInfo' AND file_ref_srl = t1.pet_srl order by file_srl limit 1 ) AS pet_img , intro , pet_cd , pet_size , spec1 , spec2 , spec3 , spec4 , spec5 , reg_dt FROM pet_info t1 WHERE 1=1
AND pet_size = #{petSize}
AND pet_nm like '%' #{searchString} '%'
ORDER BY ${orderBy}
ORDER BY pet_srl DESC
LIMIT #{rowStart},#{rowSize}

/** selectPetInfo **/ SELECT pet_srl , pet_nm , pet_cd , pet_size , intro , feature , care , feed , spec1 , spec2 , spec3 , spec4 , spec5 , link
,(SELECT file_sys_nm FROM com_file_data WHERE file_tp = 'petInfo' AND file_ref_srl = t1.pet_srl order by file_srl limit 1 ) AS pet_img
, reg_dt , upt_dt FROM pet_info t1 WHERE pet_srl = #{petSrl}

mapper.insert:
/** insertPetInfo **/
INSERT INTO pet_info(
	 pet_nm
	, pet_cd
	, pet_size
	, intro
	, feature
	, care
	, feed
	, spec1
	, spec2
	, spec3
	, spec4
	, spec5
	, link
	, reg_dt
	, upt_dt
)VALUES(
	 #{petNm}
	, #{petCd}
	, #{petSize}
	, #{intro}
	, #{feature}
	, #{care}
	, #{feed}
	, #{spec1}
	, #{spec2}
	, #{spec3}
	, #{spec4}
	, #{spec5}
	, #{link}
	, SYSDATE()
	, SYSDATE()
)

mapper.update:
/** updatePetInfo **/
UPDATE pet_info SET 
	pet_nm = #{petNm}
	, pet_cd = #{petCd}
	, pet_size = #{petSize}
	, intro = #{intro}
	, feature = #{feature}
	, care = #{care}
	, feed = #{feed}
	, spec1 = #{spec1}
	, spec2 = #{spec2}
	, spec3 = #{spec3}
	, spec4 = #{spec4}
	, spec5 = #{spec5}
	, link = #{link}
	,upt_dt = SYSDATE()
WHERE pet_srl = #{petSrl}
