mapper.select:
/** selectPetInfoCount **/
SELECT count(1) FROM pet_info WHERE 1=1
AND pet_nm like '%' #{searchString} '%'

/** selectPetInfoList **/ SELECT pet_srl , pet_nm , pet_cd , pet_size , spec1 , spec2 , spec3 , spec4 , spec5 , spec6 , reg_dt FROM pet_info t1 WHERE 1=1
AND pet_nm like '%' #{searchString} '%'
ORDER BY pet_srl DESC LIMIT #{rowStart},#{rowSize}

/** selectPetInfo **/ SELECT pet_srl , pet_nm , pet_cd , pet_size , intro , feature , care , feed , spec1 , spec2 , spec3 , spec4 , spec5 , spec6 , etc
,(select min(file_srl) file_srl from com_file_data where file_tp = 'petInfo' and file_ref_srl = t1.pet_srl) file_srl
, reg_dt , upt_dt FROM pet_info t1 WHERE pet_srl = #{petSrl}

mapper.insert:
/** insertPetInfo **/
INSERT INTO pet_info(
	 pet_nm
	, pet_cd
	, pet_size
	, intro
	, feature
	, care
	, feed
	, spec1
	, spec2
	, spec3
	, spec4
	, spec5
	, spec6
	, etc
	, reg_dt
	, upt_dt
)VALUES(
	 #{petNm}
	, #{petCd}
	, #{petSize}
	, #{intro}
	, #{feature}
	, #{care}
	, #{feed}
	, #{spec1}
	, #{spec2}
	, #{spec3}
	, #{spec4}
	, #{spec5}
	, #{spec6}
	, #{etc}
	, SYSDATE()
	, SYSDATE()
)

mapper.update:
/** updatePetInfo **/
UPDATE pet_info SET 
	pet_nm = #{petNm}
	, pet_cd = #{petCd}
	, pet_size = #{petSize}
	, intro = #{intro}
	, feature = #{feature}
	, care = #{care}
	, feed = #{feed}
	, spec1 = #{spec1}
	, spec2 = #{spec2}
	, spec3 = #{spec3}
	, spec4 = #{spec4}
	, spec5 = #{spec5}
	, spec6 = #{spec6}
	, etc = #{etc}
	,upt_dt = SYSDATE()
WHERE pet_srl = #{petSrl}
